usage.code:
import { StatsCard } from '@prototype/components'
import { ClipboardList, Users } from 'lucide-react'

// Gradient hero stat with badge
<StatsCard
  label="Total Orders"
  value={42}
  icon={ClipboardList}
  badge="12 today"
  variant="gradient"
  color="cyan"
/>

// Default stat with trend
<StatsCard
  label="Active Users"
  value={1234}
  icon={Users}
  trend={{ value: 8, direction: 'up' }}
  variant="default"
  color="teal"
/>

// Simple stat without icon/trend
<StatsCard
  label="Revenue"
  value="$45,231"
/>
