schema:
// Prisma schema file
// https://pris.ly/d/prisma-schema

generator client {
  provider = "prisma-client-js"
}

datasource db {
  provider = "sqlite"
}

// Note: LLM Boilerplate is file-driven and doesn't require a database by default.
// This schema is provided as a template for projects that need persistence.
// Set DATABASE_URL in .env file, e.g.: DATABASE_URL="file:./dev.db"

// Example models - customize for your project:

model User {
  id        String   @id @default(cuid())
  email     String   @unique
  name      String?
  role      String   @default("user")
  createdAt DateTime @default(now())
  updatedAt DateTime @updatedAt
}

model Project {
  id          String   @id @default(cuid())
  name        String
  slug        String   @unique
  description String?
  createdAt   DateTime @default(now())
  updatedAt   DateTime @updatedAt
}
